mapper.select:
SELECT a.id AS articleId, a.id, a.platform, a.title, gnc. NAME AS categoryName, a.update_time, ( SELECT COUNT(ggc.id) FROM g_news_article b LEFT JOIN g_global_correlation ggc ON ggc.ref_id = b.id AND ggc.ref_type = 4 and ggc.delete_flag=0 WHERE a.id = b.id ) AS shares, ( SELECT COUNT(ggc.id) FROM g_news_article b LEFT JOIN g_global_correlation ggc ON ggc.ref_id = b.id AND ggc.ref_type = 5 and ggc.delete_flag=0 WHERE a.id = b.id ) AS likes, ( SELECT COUNT(ggc.id) FROM g_news_article b LEFT JOIN g_global_correlation ggc ON ggc.ref_id = b.id AND ggc.ref_type = 6 and ggc.delete_flag=0 WHERE a.id = b.id ) AS browses, CONCAT( a.start_date, "-", a.end_date ) AS validDate, CASE WHEN a.start_date > NOW() THEN "2" WHEN a.start_date<= NOW() && a.end_date>=now() THEN "1" ELSE "3" END AS `status` FROM g_news_article a LEFT JOIN g_news_category gnc ON gnc.id = a.category_id WHERE 1 = 1 AND a.delete_flag = 0
group by a.id ORDER BY a.id desc
limit ${start},${end}

SELECT a.id, a.name as label, if(a.level=1,0,1) as isFirstLevel FROM g_news_category a WHERE a.delete_flag = 0
and a.level = #{level}
and a.ref_id = #{refId}
