mapper.sql:
where
a.is_delete = 'N'

    and a.id = #{_id}


    and a.sku_id = #{_sku_id}


    and a.order_id = #{_order_id}


    and a.batch_number = #{_batch_number}


    and a.stock = #{_stock}


    and a.measure_unit = #{_measure_unit}


    and a.price = #{_price}


    and a.amount = #{_amount}


    and a.status = #{_status}


    and a.remark = #{_remark}


    and a.create_time >= concat(#{startDate},' ','00:00:00')


    and a.create_time <= concat(#{endDate},' ','23:59:59')


    and a.creator = #{_creator}


    and a.update_time = #{_update_time}


    and a.updator = #{_updator}

mapper.select:
SELECT woa.id, woa.order_no, wcp.name as comb, woa.amount, woa.enter_library_time, su.username as create_order_user, GROUP_CONCAT(ws.name) as sku_name, GROUP_CONCAT(a.batch_number) as batch_number, GROUP_CONCAT(a.stock) as stock, GROUP_CONCAT(a.measure_unit) as measure_unit, GROUP_CONCAT(a.price) as price, case woa.status when '0' then '不同意' when '1' then '同意' else '未知' end as status, woa.create_time FROM w_order_add_sku a left join w_order_add woa ON woa.id = a.order_id left join w_cmn_provider wcp ON wcp.id = woa.comb left join w_sku ws ON ws.id = a.sku_id left join sys_user su ON su.id = woa.create_order_user
and woa.id is not null group by woa.id
limit #{start},#{end}
