mapper.select:
SELECT a.*, r.role_name AS rolename, CONCAT(a.mobile, '-', a.username) AS numbername, r.role_id_list FROM g_user a LEFT JOIN ( SELECT a.userid, GROUP_CONCAT(b.role_name) role_name, GROUP_CONCAT(b.id) role_id_list FROM sys_role_user a LEFT JOIN sys_role b on a.roleid = b.id GROUP BY a.userid ) r on a.id = r.userid WHERE 1=1 and a.delete_flag = 0
ORDER BY a.id asc
limit #{start},#{end}

mapper.update:
update sys_user
set rytoken = #{rytoken}
where id = #{id}

update sys_user
set check_flag = #{checkFlag}
where id = #{id}

update sys_user
set pid = #{pid}
where id = #{id}

mapper.select:
SELECT a.* FROM g_user a WHERE a. delete_flag= 0
and a.username = #{username}
and a.moile = #{moile}
and password = password(#{password})

SELECT a.*, d.dept_name AS deptname, r.role_name AS rolename, b.username AS pname, CONCAT(a.number, '-', a.username) AS numbername, r.role_id_list, COUNT(DISTINCT ois.id) AS in_store_num, COUNT(DISTINCT oss.id) AS out_store_num, COUNT(DISTINCT rg.id) AS return_store_num, COUNT(DISTINCT wlt.id) AS transfer_store_num, COUNT(DISTINCT wps.id) AS wps_store_num, COUNT(DISTINCT wic.id) AS wic_store_num FROM sys_user a LEFT JOIN sys_user b ON a.pid = b.id LEFT JOIN sys_dept d ON a.deptid = d.id LEFT JOIN ( SELECT a.userid, GROUP_CONCAT(b.role_name) role_name, GROUP_CONCAT(b.id) role_id_list FROM sys_role_user a LEFT JOIN sys_role b ON a.roleid = b.id GROUP BY a.userid ) r ON a.id = r.userid LEFT JOIN w_order_in_store ois ON ois.creator = a.id AND ois.`status` = 2
and DATE(ois.create_time) >= #{startTime}
and DATE(ois.create_time) <= #{endTime}
LEFT JOIN w_order_sale_sku oss ON oss.creator = a.id AND oss.`status` = 50
and DATE(oss.create_time) >= #{startTime}
and DATE(oss.create_time) <= #{endTime}
LEFT JOIN w_return_goods rg ON rg.creator = a.id
and DATE(rg.create_time) >= #{startTime}
and DATE(rg.create_time) <= #{endTime}
LEFT JOIN w_location_transfer wlt ON wlt.creator = a.id
and DATE(wlt.create_time) >= #{startTime}
and DATE(wlt.create_time) <= #{endTime}
LEFT JOIN w_inventory_check wic ON wic.creator = a.id
and DATE(wic.create_time) >= #{startTime}
and DATE(wic.create_time) <= #{endTime}
LEFT JOIN w_productin_spection wps ON wps.creator = a.id
and DATE(wps.create_time) >= #{startTime}
and DATE(wps.create_time) <= #{endTime}
WHERE 1 = 1 AND a.is_delete = 'N'
GROUP BY a.id ORDER BY a.id DESC
limit #{start},#{end}

mapper.update:
update sys_user
set deptid = #{deptid}
where account = #{account}

update sys_user
set wopenid  = NULL
where wopenid = #{wopenid} and username = #{username}
